var weight = msg.payload;
var stableSize = 5;
var duration = 0;
var operation_name_income = "Приход (авто)";
var maxWeight = flow.get('maxWeight');
var maxDuration = flow.get('maxDuration');
var prevTransaction = transaction;
var minWeight = flow.get('minWeight');
var minTriggerWeight = flow.get('minTriggerWeight');
var is_minus = false;
var transactionAccWeight = flow.get('transactionAccWeight');//облікова вага
var overLoadWeight = flow.get('overLoadWeight');

//transaction
if (prevWeight === null){
    prevWeight = msg.payload;    
}else{
    if((prevWeight <= 0) && (weight > 0) && (weight > minTriggerWeight)){
        //start of transaction
        transaction = Date.now();
        msg.on = true;
        flow.set('transaction',transaction);
    }else if((prevWeight > 0) && (weight <=0) && (transaction)){
        //transaction = 0;
        msg.on = false;
        transactionData = [];
        flow.set('transaction',0);
        flow.set('transactionMaxWeight',null);
        flow.set('transactionHasMaxWeight',null);
        flow.set('transactionHasMinWeight',null);
        flow.set('transactionHasMaxDuration',null);
        flow.set('transactionHasOverLoad',null);
        flow.set('transactionOperationName',null);
        flow.set('transactionWaybill',null);
        flow.set('transactionMetadata',null);
        flow.set('transactionAccWeight', null);//облікова вага
    }else if((transaction === null) && (weight>0) && (weight > minTriggerWeight)){
        transaction = Date.now();
        flow.set('transaction',transaction);
    }
    if((prevWeight < 0) && ((weight-prevWeight) > minTriggerWeight)){
        //start of transaction
        is_minus = true;
        msg.prevWeight = prevWeight;
    }
}

if (transaction > 0){
    transactionData.push(weight);    

    //stable
    if(transactionData.length > stableSize+1){
        var stabletmp = true;
        for (var i = transactionData.length-1; i > transactionData.length-1-stableSize; i--){
            if (transactionData[i] !== transactionData[i-1]){
                stabletmp = false;
                msg.stable = stabletmp;
                break;
            }
            msg.stable = stabletmp;
        }
    }else{
        msg.stable = false;
    }

    //duration & transactionMaxWeight
    duration = (Number(Date.now()) - Number(transaction));
    if( (weight>transactionMaxWeight) || (transactionMaxWeight === null)){
        transactionMaxWeight = weight;
        flow.set("transactionMaxWeight",transactionMaxWeight);
    }

    if(!transactionHasMaxWeight && weight > maxWeight){
        msg.transactionHasMaxWeight = true;
        flow.set('transactionHasMaxWeight',true);
    }

    if(!transactionHasMinWeight && weight < minWeight){
        msg.transactionHasMinWeight = true;
        flow.set('transactionHasMinWeight',true);
    }

    //max duartion
    if(!transactionHasMaxDuration && duration > maxDuration){
        msg.transactionHasMaxDuration = true;
        flow.set('transactionHasMaxDuration',true);
    }
}

//result
